//! This module tests the library's analysis capabilities on the `House`
//! contract`.
#![cfg(test)]

use storage_layout_extractor::{
    tc::abi::{AbiType, StructElement},
    watchdog::LazyWatchdog,
};

mod common;

/// Tests the library on the bytecode of the House contract deployed
/// [here](https://etherscan.io/address/0x7cbb952a448672eb6ea3cc869cdcec6cf6163e29#code).
#[test]
fn correctly_generates_a_layout() -> anyhow::Result<()> {
    // Create the extractor
    let bytecode = "";
    let extractor = common::new_extractor_from_bytecode(bytecode, LazyWatchdog.in_rc())?;

    // Get the final storage layout for the input contract
    let layout = extractor.analyze()?;

    // We should see 36 entries
    assert_eq!(layout.slot_count(), 36);

    // `address` but we infer `number160`
    assert!(layout.has_slot(0, 0, AbiType::Number { size: Some(160) }));

    // `uint256` but we infer `number256`
    assert!(layout.has_slot(1, 0, AbiType::Number { size: Some(256) }));

    // `address`
    assert!(layout.has_slot(2, 0, AbiType::Address));

    // `bool` but we infer `number8`
    assert!(layout.has_slot(3, 0, AbiType::Number { size: Some(8) }));

    // `string` but we infer `bytes`
    assert!(layout.has_slot(4, 0, AbiType::DynBytes));

    // `string` but we infer `bytes`
    assert!(layout.has_slot(5, 0, AbiType::DynBytes));

    // `string` but we infer `bytes`
    assert!(layout.has_slot(6, 0, AbiType::DynBytes));

    // `address`
    assert!(layout.has_slot(7, 0, AbiType::Address));

    // `packed(address, bool)` but we infer `packed(address, number8)`
    assert!(layout.has_slot(8, 0, AbiType::Address));
    assert!(layout.has_slot(8, 160, AbiType::Number { size: Some(8) }));

    // `uint256`
    assert!(layout.has_slot(9, 0, AbiType::UInt { size: Some(256) }));

    // `uint256`
    assert!(layout.has_slot(10, 0, AbiType::UInt { size: Some(256) }));

    // `uint256` but we miss it entirely
    assert!(layout.has_no_slot_at(11));

    // `string` but we infer `bytes`
    assert!(layout.has_slot(12, 0, AbiType::DynBytes));

    // `mapping(uint256 => struct)` but we infer `mapping(number256 => struct)` but
    // the struct is not quite the same
    assert!(layout.has_slot(
        13,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Number { size: Some(256) }),
            value_type: Box::new(AbiType::Struct {
                elements: vec![
                    StructElement::new(0, AbiType::Number { size: Some(256) }),
                    StructElement::new(256, AbiType::Address),
                    StructElement::new(416, AbiType::Number { size: Some(8) }),
                    StructElement::new(512, AbiType::Bytes { length: Some(32) }),
                    StructElement::new(768, AbiType::Bytes { length: Some(32) }),
                    StructElement::new(1024, AbiType::Bytes { length: Some(32) }),
                    StructElement::new(1280, AbiType::Number { size: Some(8) }),
                    StructElement::new(1288, AbiType::Bytes { length: Some(31) }),
                    StructElement::new(1536, AbiType::UInt { size: Some(256) }),
                    StructElement::new(1792, AbiType::UInt { size: None }),
                    StructElement::new(2048, AbiType::Number { size: Some(8) }),
                    StructElement::new(2056, AbiType::Bytes { length: Some(31) }),
                    StructElement::new(2304, AbiType::UInt { size: Some(256) }),
                    StructElement::new(2560, AbiType::UInt { size: Some(256) }),
                    StructElement::new(2816, AbiType::Number { size: Some(256) }),
                    StructElement::new(3072, AbiType::Bytes { length: Some(20) }),
                    StructElement::new(3232, AbiType::Bytes { length: Some(1) }),
                    StructElement::new(3240, AbiType::Any),
                ],
            }),
        }
    ));

    // `uint256` but we infer `numberUnknown`
    assert!(layout.has_slot(14, 0, AbiType::Number { size: None }));

    // `uint256` but we infer `numberUnknown`
    assert!(layout.has_slot(15, 0, AbiType::Number { size: None }));

    // `mapping(uint256 => uint256)` but we infer `mapping(number256 => uint256)`
    assert!(layout.has_slot(
        16,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Number { size: Some(256) }),
            value_type: Box::new(AbiType::UInt { size: Some(256) }),
        }
    ));

    // `mapping(uint256 => uint256)` but we infer `mapping(number256 =>
    // numberUnknown)`
    assert!(layout.has_slot(
        17,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Number { size: Some(256) }),
            value_type: Box::new(AbiType::Number { size: None }),
        }
    ));

    // `mapping(uint256 => uint256)` but we infer `mapping(number256 =>
    // uintUnknown)`
    assert!(layout.has_slot(
        18,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Number { size: Some(256) }),
            value_type: Box::new(AbiType::UInt { size: None }),
        }
    ));

    // `mapping(uint256 => mapping(uint256 => uint256))` but we infer
    // `mapping(number256 => mapping(bytes32 => uint256))`
    assert!(layout.has_slot(
        19,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Number { size: Some(256) }),
            value_type: Box::new(AbiType::Mapping {
                key_type:   Box::new(AbiType::Bytes { length: Some(32) }),
                value_type: Box::new(AbiType::UInt { size: Some(256) }),
            }),
        }
    ));

    // `mapping(address => mapping(uint256 => uint256))` but we infer
    // `mapping(address => mapping(number256 => uint256))`
    assert!(layout.has_slot(
        20,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Address),
            value_type: Box::new(AbiType::Mapping {
                key_type:   Box::new(AbiType::Number { size: Some(256) }),
                value_type: Box::new(AbiType::UInt { size: Some(256) }),
            }),
        }
    ));

    // `mapping(address => mapping(uint256 => uint256))` but we infer
    // `mapping(address => mapping(number256 => uintUnknown))`
    assert!(layout.has_slot(
        21,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Address),
            value_type: Box::new(AbiType::Mapping {
                key_type:   Box::new(AbiType::Number { size: Some(256) }),
                value_type: Box::new(AbiType::UInt { size: None }),
            }),
        }
    ));

    // `mapping(address => mapping(uint256 => mapping(uint256 => uint256)))` but we
    // infer `mapping(address => mapping(number256 => mapping(bytes32 => uint256))`
    assert!(layout.has_slot(
        22,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Address),
            value_type: Box::new(AbiType::Mapping {
                key_type:   Box::new(AbiType::Number { size: Some(256) }),
                value_type: Box::new(AbiType::Mapping {
                    key_type:   Box::new(AbiType::Bytes { length: Some(32) }),
                    value_type: Box::new(AbiType::UInt { size: Some(256) }),
                }),
            }),
        }
    ));

    // `mapping(address => mapping(uint256 => uint256))` but we infer
    // `mapping(address => mapping(number256 => uint256))`
    assert!(layout.has_slot(
        23,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Address),
            value_type: Box::new(AbiType::Mapping {
                key_type:   Box::new(AbiType::Number { size: Some(256) }),
                value_type: Box::new(AbiType::UInt { size: Some(256) }),
            }),
        }
    ));

    // `mapping(address => mapping(uint256 => uint256))` but we infer
    // `mapping(address => mapping(number256 => struct(number8, bytes31)))`
    assert!(layout.has_slot(
        24,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Address),
            value_type: Box::new(AbiType::Mapping {
                key_type:   Box::new(AbiType::Number { size: Some(256) }),
                value_type: Box::new(AbiType::Struct {
                    elements: vec![
                        StructElement::new(0, AbiType::Number { size: Some(8) }),
                        StructElement::new(8, AbiType::Bytes { length: Some(31) })
                    ],
                }),
            }),
        }
    ));

    // `mapping(address => mapping(uint256 => bool))` but we infer
    // `mapping(address => mapping(number256 => struct(number8, bytes31)))`
    assert!(layout.has_slot(
        25,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Address),
            value_type: Box::new(AbiType::Mapping {
                key_type:   Box::new(AbiType::Number { size: Some(256) }),
                value_type: Box::new(AbiType::Struct {
                    elements: vec![
                        StructElement::new(0, AbiType::Number { size: Some(8) }),
                        StructElement::new(8, AbiType::Bytes { length: Some(31) })
                    ],
                }),
            }),
        }
    ));

    // `mapping(address => uint256)` but we infer `mapping(address => uintUnknown)`
    assert!(layout.has_slot(
        26,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Address),
            value_type: Box::new(AbiType::UInt { size: None }),
        }
    ));

    // `mapping(address => uint256)` but we infer `mapping(address =>
    // numberUnknown)`
    assert!(layout.has_slot(
        27,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Address),
            value_type: Box::new(AbiType::Number { size: None }),
        }
    ));

    // `mapping(address => uint256)` but we infer `mapping(address => uintUnknown)`
    assert!(layout.has_slot(
        28,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Address),
            value_type: Box::new(AbiType::UInt { size: None }),
        }
    ));

    // `mapping(address => uint256)` but we infer `mapping(bytes20 => numberNone)`
    assert!(layout.has_slot(
        29,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Bytes { length: Some(20) }),
            value_type: Box::new(AbiType::Number { size: None }),
        }
    ));

    // `address[]` but we infer `bytes20[]`
    assert!(layout.has_slot(
        30,
        0,
        AbiType::DynArray {
            tp: Box::new(AbiType::Bytes { length: Some(20) }),
        }
    ));

    // `mapping(uint256 => bool)` but we infer `mapping(number256 => struct(number8,
    // bytes31))`
    assert!(layout.has_slot(
        31,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Number { size: Some(256) }),
            value_type: Box::new(AbiType::Struct {
                elements: vec![
                    StructElement::new(0, AbiType::Number { size: Some(8) }),
                    StructElement::new(8, AbiType::Bytes { length: Some(31) })
                ],
            }),
        }
    ));

    // `mapping(uint256 => uint256)` but we infer `mapping(number256 =>
    // uintUnknown)`
    assert!(layout.has_slot(
        32,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Number { size: Some(256) }),
            value_type: Box::new(AbiType::UInt { size: None }),
        }
    ));

    // `mapping(uint256 => uint256)` but we infer `mapping(number256 =>
    // uintUnknown)`
    assert!(layout.has_slot(
        33,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Number { size: Some(256) }),
            value_type: Box::new(AbiType::UInt { size: None }),
        }
    ));

    // `uint256` but we infer `numberUnknown`
    assert!(layout.has_slot(34, 0, AbiType::Number { size: None }));

    // `mapping(address => uint256)` but we infer `mapping(address =>
    // numberUnknown)`
    assert!(layout.has_slot(
        35,
        0,
        AbiType::Mapping {
            key_type:   Box::new(AbiType::Address),
            value_type: Box::new(AbiType::Number { size: None }),
        }
    ));

    Ok(())
}
