//! This module tests the library's analysis capabilities on the
//! `MortgageRender` contract`.
#![cfg(test)]

use storage_layout_extractor::watchdog::LazyWatchdog;

mod common;

/// Tests the library on the bytecode of the MortgageRender contract deployed
/// [here](https://etherscan.io/address/0xf5a5fe458ec5ad8db8fe746679fa80cf1e7d7592).
#[test]
fn correctly_generates_a_layout() -> anyhow::Result<()> {
    // Create the extractor
    let bytecode = "";
    let extractor = common::new_extractor_from_bytecode(bytecode, LazyWatchdog.in_rc())?;

    // Get the final storage layout for the input contract
    let layout = extractor.analyze()?;

    // We should have no slots
    assert!(layout.is_empty());

    Ok(())
}
